servoList = [10, 9, 8, 7]

angleList = [0, 0 ,0 ,0]

def rotateServoToAngle(servo, angle):
    servokey = servo-1
    for i in range(int(np.floor(angleList[servokey])), int(np.floor(angle))+1):
#        board.digital[servoList[servokey]].write(i)
        sleep(0.1)
 #   board.digital[servoList[servokey]].write(angle)
    angleList[servokey] = angle

